$('.owl-carousel').owlCarousel({
    loop:true,
    margin:10,
    nav:true,
    autoplay:true,
    autoplayTimeout:3000,
    autoplayHoverPause:false,
    center:true,
    responsive:{
        0:{
            items:1
        },
        1:{
            items:1
        },
        2:{
            items:1
        }
        

    }
})
